import React from 'react';
import styled from "styled-components";
import Header from './components/Header';
import PlaylistManagerPage from './components/PlaylistManagerPage';
import PlaylistCreationForm from './components/PlaylistCreationForm';

const AppContainer = styled.div`
        display: flex;
        flex-direction: column;
        width: 100vw;
        height: 100vh;
`

class App extends React.Component {

  state = {
    currentPage: "playlistCreationForm"
  }

  render () {   
    const renderCurrentPage = () => {
      if (this.state.currentPage === "playlistCreationForm") {
        return <PlaylistCreationForm />
      } else if (this.state.currentPage === "playlistManagerPage") {
        return <PlaylistManagerPage />
      }

    }



    return (
      <AppContainer>
        <Header />
        {renderCurrentPage()}
        </AppContainer>
    );


  }

}

export default App;
