import React from 'react'
import styled from "styled-components"

const ButtonContainer = styled.div`
     display: flex;
     justify-content: space-between;
     width: 300px;
     margin-top: 50px;
`

perfilDiferente = () => {
    const urlPerfil = "https://us-central1-missao-newton.cloudfunctions.net/astroMatch/:antonio-machado/person"
    
    axios.get(urlPerfil)
    .then((res) => {
        console.log(res)
        this.setState({bio: res.data.profile.bio})
        this.setState({foto: res.data.profile.photo})
        this.setState({nome: res.data.profile.name})
        this.setState({idade: res.data.profile.age})
       

    })
    .catch((err) => {
        console.log(err);
    });

};

novoPerfil = () => {
    const urlPerfil = "https://us-central1-missao-newton.cloudfunctions.net/astroMatch/:antonio-machado/person"
    
    axios.get(urlPerfil)
    .then((res) => {
        this.setState({bio: res.data.profile.bio})
        this.setState({foto: res.data.profile.photo})
        this.setState({nome: res.data.profile.name})
        this.setState({idade: res.data.profile.age})
       

    })
    .catch((err) => {
        console.log(err);
    });

};

const Botoes = () => {
    return (
        <ButtonContainer>

           <button>Tela de Matches</button>
           <button onClick={perfilDiferente()}>Ver Outro Perfil</button>
            

        </ButtonContainer>
    )

}

export default Botoes
