import React from 'react'
import styled from "styled-components"
import axios from "axios"


const ButtonContainer = styled.div`
     display: flex;
     justify-content: space-between;
     width: 300px;
     margin-top: 50px;
`

export default class App extends React.Component {
    state = {
        photo: "",
        name: "",
        bio: "",
        age: ""

    }

    componentDidMount() {
        this.novoPerfil();
    }

    novoPerfil = () => {
        const url = "https://us-central1-missao-newton.cloudfunctions.net/astroMatch/:antonio/person"
        
        axios.get(url)
        .then((res) => {
            this.setState({bio: res.data.profile.bio})
            this.setState({foto: res.data.profile.photo})
            this.setState({nome: res.data.profile.name})
            this.setState({idade: res.data.profile.age})
           

        })
        .catch((err) => {
            console.log(err);
        });
    
    };

    render () {
        console.log(this.state.nome)
    return (
      <div>`Nome: {this.state.nome}, Idade: {this.state.idade}, Bio: {this.state.bio}, Foto:  {this.state.foto}`
      <ButtonContainer>
      <button>Dar Match</button>
      <button>Ver outro Perfil</button>
      </ButtonContainer>
      </div>
    )

    }
}
