import React from 'react'
import styled from "styled-components"
import axios from "axios"


const ButtonContainer = styled.div`
     display: flex;
     justify-content: space-between;
     width: 300px;
     margin-top: 50px;
`

export default class App extends React.Component {
    state = {
        isMatch: ""

    }

    componentDidMount() {
        this.darMatch();
    }

    


    darMatch = () => {

        const urlBotoes = "https://us-central1-missao-newton.cloudfunctions.net/astroMatch/:antonio-machado/choose-person"
        const bodyBotoes = {
            "id": "71gMbZs2txS9LDvGK5Ew",
            "choice": true
        }

        const headerBotoes = {
            headers: {
                Authorization: "Content-Type: application/json"
            }
        }
      
        
        axios.post(urlBotoes, bodyBotoes, headerBotoes)
        .then((res) => {
            console.log(res)
            this.setState({isMatch: res.data.data.isMatch})
                 

        })
        .catch((err) => {
            console.log(err);
        });
    
    };

    verOutroPerfil = () => {

        const urlOutroPerfil = "https://us-central1-missao-newton.cloudfunctions.net/astroMatch/:antonio/person"

      
        
        axios.post(urlOutroPerfil)
        .then((res) => {
            console.log(res)
            this.setState({isMatch: res.data.data.isMatch})
                 

        })
        .catch((err) => {
            console.log(err);
        });
    
    };



    render () {
      
    return (
      <div>
      <ButtonContainer>

   <button onClick={this.darMatch()}>Dar Match</button>
      <button>Ver outro perfl</button>
      </ButtonContainer>
      </div>
    )

    }
}
