import React from 'react'

import axios from "axios"




export default class App extends React.Component {
    state = {
        photo: "",
        name: "",
        bio: "",
        age: ""

    }

    componentDidMount() {
        this.novoPerfil();
    }

    novoPerfil = () => {
        const url = "https://us-central1-missao-newton.cloudfunctions.net/astroMatch/:antonio/person"
        
        axios.get(url)
        .then((res) => {
            this.setState({bio: res.data.profile.bio})
            this.setState({foto: res.data.profile.photo})
            this.setState({nome: res.data.profile.name})
            this.setState({idade: res.data.profile.age})
            console.log(res)

        })
        .catch((err) => {
            console.log(err);
        });
    
    };

    render () {
        console.log(this.state.photo)
    return (
      <div></div>
    )

    }
}
